import _color from '../../utils/color.js'
export default {
  data:()=>({
    active:false,
    type:'default',
    color:null,
    background:'rgba(255,255,255,.6)',
    src:'',
    leftx:0,
    topx:0,
    clickEffect:false,
    activeEffectClick:false,
    scale:1,
    textAfter:false,
    text:null,
  }),
  computed:{
    styleEffectClick(){
      return {
        left: `${this.leftx}px`,
        top: `${this.topx}px`
      }
    },
    styleEffect1(){
      let style = {
        borderLeft: `3px solid ${_color.getColor(this.color,1)}`
      }

      if(this.type == 'border'){
        style = {
          borderLeft: `1px solid ${_color.getColor(this.color,1)}`
        }
      }

      if(this.type == 'point'){
        style = {
          background: _color.getColor(this.color,.4)
        }
      }

      if(this.type == 'radius'){
        style = {
          border: `3px solid ${_color.getColor(this.color,1)}`
        }
      }

      if(this.type == 'corners'){
        style = {
          border: `3px solid ${_color.getColor(this.color,1)}`
        }
      }

      if(this.type == 'sound'){
        style = {
          background: _color.getColor(this.color,1)
        }
      }

      return style
    },
    styleEffect2(){
      let style = {
        borderLeft: `3px solid ${_color.getColor(this.color,1)}`
      }

      if(this.type == 'border'){
        style = {
          borderLeft: `1px solid ${_color.getColor(this.color,1)}`
        }
      }

      if(this.type == 'point'){
        style = {
          background: _color.getColor(this.color,.4)
        }
      }

      if(this.type == 'radius'){
        style = {
          border: `3px solid ${_color.getColor(this.color,1)}`
        }
      }

      if(this.type == 'corners'){
        style = {}
      }

      if(this.type == 'sound'){
        style = {
          background: _color.getColor(this.color,1)
        }
      }

      return style
    },
    styleEffect3(){
      let style = {
        borderLeft: `3px solid ${_color.getColor(this.color,1)}`
      }

      if(this.type == 'border'){
        style = {
          borderLeft: `1px solid ${_color.getColor(this.color,1)}`
        }
      }

      if(this.type == 'point'){
        style = {
          background: _color.getColor(this.color,.4)
        }
      }

      if(this.type == 'radius'){
        style = {
          border: `3px solid ${_color.getColor(this.color,1)}`
        }
      }

      if(this.type == 'corners'){
        style = {}
      }

      if(this.type == 'sound'){
        style = {
          background: _color.getColor(this.color,1)
        }
      }

      return style
    },
    style(){
      return {
        background:_color.getColor(this.background,1)
      }
    }
  },
  mounted(){
    this.active = true
  },
  methods:{
    effectClick(evt){
      this.leftx = evt.offsetX
      this.topx = evt.offsetY
      this.activeEffectClick = true
      setTimeout(()=>{
        this.activeEffectClick = false
      },50)
    },
    close(){
      this.active = false
    }
  }
}
